    targetSignalfxDashboards: [{
        isDefault: true,
        name: "sfx_dash",
        dashboardGroupId: signalfx_dashboard_group.mydashboardgroup0.id,
        dashboardId: signalfx_dashboard.mydashboard0.id,
    }],
});
// A dashboard-specific link to an external URL
