    contextDashboardId: signalfx_dashboard.mydashboard0.id,
    propertyName: "pname2",
    propertyValue: "pvalue",
    targetExternalUrls: [{
        name: "ex_url",
        timeFormat: "ISO8601",
        url: "https://www.example.com",
        propertyKeyMapping: {
            foo: "bar",
        },
    }],
});
